import { useState } from 'react';
import { DateRange ,DateRangePicker} from 'react-date-range';
import 'react-date-range/dist/styles.css';
import 'react-date-range/dist/theme/default.css';
import { addDays } from 'date-fns';


interface Props{
    title?:string,
    startDate?: Date  ,
    end?:Date ,
    allDay?: boolean | null
}

const events = [
    {
        title: "Big Meeting",
        allDay: true,
        startDate: new Date(2021, 10, 0),
        end: new Date(2021, 10, 0),
    },
    {
        title: "Vacation",
        startDate: new Date(2021, 11, 7),
        end: new Date(2021, 11, 10),
    },
    {
        title: "Conference",
        startDate: new Date(2021, 10, 20),
        end: new Date(2021, 10, 23),
    },
];


function Calendar(){
    const [state, setState] = useState({
        selection: {
            startDate: new Date(),
            endDate: new Date(),
            key: 'selection'
        }
        });
    const [newEvent, setNewEvent] = useState({title:"", startDate:new Date(), end: new Date()})
    const [allEvents, setAllEvents] = useState(events)    

    function handleEvent(){
        setAllEvents([...allEvents, newEvent])
    }

    return(
        <DateRange
            editableDateInputs={true}
            onChange={(item: Date | any) => {
                setState({ ...state, ...item })
                setNewEvent({ ...newEvent, startDate:item }) 
                handleEvent()
            }}
            moveRangeOnFirstSelection={false}
            ranges={[state.selection]}
        />
    );
}

export default Calendar;

