import * as React from 'react';
import Box from '@mui/material/Box';
import AdapterDateFns from '@mui/lab/AdapterDateFns';
import LocalizationProvider from '@mui/lab/LocalizationProvider';
import DesktopDatePicker from '@mui/lab/DatePicker';
import Grid from '@mui/material/Grid';
import Button from '@mui/material/Button';

export default function CustomInput() {
  const [value, setValue] = React.useState<Date | null | number|any>(new Date());
  const [value1, setValue1] = React.useState<Date | null | number|any>(new Date());

  const handleDate = (newValue:Date|null|number) => {
                            setValue1(newValue);
  }

  return (
    <LocalizationProvider dateAdapter={AdapterDateFns}>
        <Grid container component="main" sx={{ height: '10vh' }}>
            <Grid container>
                <Grid item xs={12}>
                    <DesktopDatePicker
                        label="Custom input"
                        value={value}
                        onChange={(newValue:Date|null|number|any) => {
                            setValue(newValue);
                            console.log(value.getTime())
                        }}
                        renderInput={({ inputRef, inputProps, InputProps }) => (
                        <Box sx={{ display: 'flex', alignItems: 'center' }}>
                            <input ref={inputRef} {...inputProps} />
                            {InputProps?.endAdornment}
                        </Box>
                        )}
                    />
                </Grid>

                <Grid item>
                    <DesktopDatePicker
                        label="Custom input"
                        value={value1}
                        onChange={(newValue:Date|null|number|any) => {
                            setValue1(newValue);
                            console.log(value.getTime())
                        }}
                        renderInput={({ inputRef, inputProps, InputProps }) => (
                        <Box sx={{ display: 'flex', alignItems: 'center' }}>
                            <input ref={inputRef} {...inputProps} />
                            {InputProps?.endAdornment}
                        </Box>
                        )}
                    />
                </Grid>
            </Grid>
            {value.getTime()-value1.getTime() ? null: <Button variant="contained" color="success" sx={{ mt: 1, mb: 2 }}>
                Submit
            </Button>}

        </Grid>
    </LocalizationProvider>
  );
}
