    //     endDate: null,
    //     key: 'selection'
    //     }
    // ]);

    // return(
    //     <DateRangePicker
    //     editableDateInputs={true}
    //     onChange={(item : Date | null | any) => setState([item.selection])}
    //     moveRangeOnFirstSelection={false}
    //     ranges={[state.key]}
    //     />
    // );

    const [state, setState] = useState({
        selection: {
            startDate: new Date(),
            endDate: null,
            key: 'selection'
        },
        compare: {
            startDate: new Date(),
            endDate: addDays(new Date(), 3),
            key: 'compare'
        }
        });

    return (
        <DateRangePicker
            onChange={item => setState({ ...state, ...item })}
            months={1}
            minDate={addDays(new Date(), -300)}
            maxDate={addDays(new Date(), 900)}
            direction="vertical"
            scroll={{ enabled: true }}
            ranges={[state.selection, state.compare]}
        />
        );
}

export default Calendar;
